// src/components/UploadForm.js
import React, { useState } from "react";
import axios from "axios";

const UploadForm = () => {
    const [file, setFile] = useState(null);
    const [data, setData] = useState(null);
    const [error, setError] = useState("");

    const handleFileChange = (e) => {
        setFile(e.target.files[0]);
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        if (!file) {
            setError("Please select a file.");
            return;
        }

        const formData = new FormData();
        formData.append("document", file);

        try {
            const response = await axios.post("http://localhost:5000/api/upload", formData, {
                headers: { "Content-Type": "multipart/form-data" },
            });

            // Log the response data to verify its structure and content
            console.log("Response Data:", response.data);

            setData(response.data);
            setError("");
        } catch (err) {
            console.error(err);
            setError("Failed to extract data from document.");
        }
    };


    return (
        <div style={{ padding: "20px", maxWidth: "500px", margin: "auto" }}>
            <h2>Upload Document</h2>
            <form onSubmit={handleSubmit}>
                <input type="file" accept="image/*" onChange={handleFileChange} />
                <button type="submit">Upload and Extract</button>
            </form>

            {error && <p style={{ color: "red" }}>{error}</p>}

            {data && (
                <div style={{ marginTop: "20px" }}>
                    <h3>Extracted Information</h3>
                    <p><strong>Name:</strong> {data.name || "N/A"}</p>
                    <p><strong>Document Number:</strong> {data.documentNumber || "N/A"}</p>
                    <p><strong>Expiration Date:</strong> {data.expirationDate || "N/A"}</p>
                </div>
            )}
        </div>
    );
};

export default UploadForm;
